mapper.select:
SELECT * FROM ( SELECT rownum rnum, B.* FROM ( SELECT board.*, member.m_nick , ( SELECT count(*) FROM recommend WHERE recommend.b_no = board.b_no ) AS RECOMMEND, ( SELECT count(*) FROM comments WHERE comments.b_no = board.b_no AND c_delete_state = 'Y' ) AS COMMENTS, ( SELECT count(*) FROM commentss WHERE commentss.c_no IN( SELECT c_no FROM comments WHERE comments.b_no = board.b_no ) ) AS COMMENTSS FROM board, member WHERE b_grade = 'B' AND board.m_no = member.m_no ORDER BY b_no DESC ) B WHERE 1=1
AND REGEXP_LIKE ( b_title, #{search} ) OR REGEXP_LIKE ( b_content, #{search} )
AND REGEXP_LIKE ( b_title, #{search} )
AND REGEXP_LIKE ( b_content, #{search} )
AND 1=1
) BOARD WHERE rnum BETWEEN #{paging.startNo} AND #{paging.endNo}

mapper.update:
UPDATE board
SET b_Hit = b_Hit + 1
WHERE b_no = #{bNo}

mapper.select:
SELECT board.*, member.m_nick, (
	SELECT count(*) FROM comments
	WHERE b_no = #{bNo}
)AS COMMENTS, (
	SELECT count(*) FROM commentss
	WHERE c_no IN(
		SELECT c_no FROM comments
		WHERE b_no = #{bNo}
	)
)AS COMMENTSS
FROM board, member
WHERE b_no = #{bNo} AND board.m_no = member.m_no

mapper.update:
UPDATE board
SET b_title = #{bTitle}, b_content = #{bContent}
WHERE b_no = #{bNo}

mapper.select:
SELECT comments.*, member.m_nick FROM member, comments
WHERE member.m_no = comments.m_no AND comments.b_no = #{bNo}
ORDER BY c_no ASC

SELECT comments.*, member.m_nick FROM member, comments
WHERE member.m_no = comments.m_no AND comments.c_no = #{cNo}
ORDER BY c_no ASC

SELECT comments.*, member.m_nick FROM member, comments
WHERE member.m_no = comments.m_no AND comments.c_no = #{cNo}
ORDER BY c_no ASC

mapper.update:
UPDATE comments
SET c_content = #{cContent}
WHERE c_no = #{cNo}

UPDATE comments
SET c_delete_state = 'N'
WHERE c_no = #{cNo}

UPDATE commentss
SET cs_content = #{csContent}
WHERE cs_no = #{csNo}
